/**
 * Creates subtitles with missing or undefined fields
 */
export function createMockSubtitlesWithMissingFields(): Subtitle[] {
	return [
		// Missing locale, only languageTag
		createMockSubtitle({
			id: 'en-001',
			locale: undefined as any,
			languageTag: 'en',
			displayLanguageName: 'English',
			format: 'vtt',
			autogenerated: false
		}),
		// Missing languageTag, only locale
		createMockSubtitle({
			id: 'es-001',
			locale: 'es',
			languageTag: undefined as any,
			displayLanguageName: 'Spanish',
			format: 'vtt',
			autogenerated: false
		}),
		// Missing format
		createMockSubtitle({
			id: 'fr-001',
			locale: 'fr',
			languageTag: 'fr',
			displayLanguageName: 'French',
			format: undefined as any,
			autogenerated: false
		})
	];
}

